getInverseMapList :: Map String [(Int, String)] -> [(String, String)]
getInverseMapList ys = concatMap (\y -> getInsertKv (fst y) (map snd (snd y))) (Map.toList ys)

getListToMap :: [(String, String)] -> Map String [String]
getListToMap = foldr (\(k,v) -> Map.insertWith (++) k [v]) Map.empty

getInsertKv :: String -> [String] -> [(String, String)]
getInsertKv val keys = [(key, val) | key <- keys ]

getPossibleParents :: Map String [String] ->  String -> [String]
getPossibleParents mapping currentBag = parents ++ concatMap (getPossibleParents mapping) parents
                                        where parents = getVals $ Map.lookup currentBag mapping

getVals :: Maybe [a] -> [a]
getVals Nothing = []
getVals (Just x) = x


getBagsInside :: Map String [(Int, String)] -> String -> Int
getBagsInside mapping bag = 1 + sum (map (\(x,y) -> x * (getBagsInside mapping y)) childeren)
                        where childeren = getVals $ Map.lookup bag mapping


part1, part2 :: Int
part1 = length $ nub $ getPossibleParents (getListToMap $ getInverseMapList inputData) "shiny gold bag"
part2 = getBagsInside inputData "shiny gold bag" - 1