{-# OPTIONS_GHC -Wno-incomplete-patterns #-}
module Day6 where

import Data.List

inputData :: [Char]
inputData = ""
 
searchMarker :: [Char] -> [Char] -> Int -> Int
searchMarker _ [] i = - 1000
searchMarker marker@(m:arker) (x:xs) i | length (nub marker) == length marker = i
                                       | otherwise = searchMarker (arker ++ [x]) xs i+1

part1, part2 :: [Char] -> Int
part1 input = searchMarker (take 4 input) (drop 4 input) 4
part2 input = searchMarker (take 14 input) (drop 14 input) 14